jQuery(document).ready(function($){
  
      $.ajax({
	   url: '../cherche_edts.php',
	   type: 'post',
	   dataType: 'json',
	   success: function(data) {
		if (data.length==0) {
			html_text="<p>Aucun emploi du temps pour le moment</p>";
		} else {
			html_text="<ul>";
		    	for (e=0; e<data.length; e++) {
				if (data[e][0] == "p") {
					type_edt=" (prÊvisionnel) ";
				} else {
					type_edt="";
				}
			html_text+="<li>PÊriode " + data[e][1] + type_edt + " du <a href='edt/" +data[e][3] +"'>"+ data[e][2] +"</a></li>";	
		 	}
			html_text +="</ul>";     
  		}
		$("#lesedts").append(html_text);
	} 
});
});
